import React ,{Component} from 'react';
import Header from './Header';
import StickyNavBar from './StickyNavBar';
import './Accomodations.css';

const img = require("../res/hotel.jpeg");
const hotel_names = [['Homewood Suites',
'https://www.hilton.com/en/hotels/atlhwhw-homewood-suites-atlanta-i-85-lawrenceville-duluth/',
'1725 Pineland Rd','Duluth, GA 30096', '(770) 931-9800'],
 ['Hampton','https://www.hilton.com/en/hotels/atlgwhx-hampton-suites-atlanta-duluth-gwinnett-county/','1775 N Brown Rd'
,'Lawrenceville, GA 30043', '(770) 277-1243']];
class Registry extends Component{

    render(){
        const hotel_div = hotel_names.map((hotel_info,key)=>{

            return(
                <div key={key} className='store-container' >
                    <a className="hotel-button" href={`${hotel_info[1]}`}>
                    View</a>
                </div>    
            )

        })
        return(

            <div id='Registry'>
                <Header/>
                <StickyNavBar/>
                <span className='capture'>We are starting small!</span>
                <span className='registry-capture-small'>And hope you could help us :)</span>
                <div className='registry-img background-tint' style={{'backgroundImage':`url('${img}')`}}/>
                <div className='registry-pane-parent'>
                    <p className='registry-pane-text'> Here are a few stores we registred wtih:</p>
                    <div className='registry-pane'>
                        {hotel_div}
                    </div>
                </div>


            </div>
        )
    }


}
export default Registry;
