import React, { Component } from 'react';
import { Slide } from 'react-slideshow-image';

const SlideImages = [
    '/couple1.jpg',
    '../src/res/couple2.jpg',
    '/res/couple3.jpg'
]

const properties = {
    duration: 5000,
    transitionDuration: 500,
    infinite: true,
    indicators: true,
    arrows: true
  }

class MainPane extends Component{

    render(){
        return(
            <Slide {...properties}>
                <div className='each-slide'>
                    <div style={{'backgroundImage': `url(${SlideImages[0]})`}}>
                    </div>
                </div><div className='each-slide'>
                    <div style={{'backgroudImage': `url(${SlideImages[1]})`}}></div>
                </div><div className='each-slide'>
                    <div style={{'backgroudImage': `url(${SlideImages[2]})`}}></div>
                </div>
            </Slide>
            )
    }

}
export default MainPane;
